class Solution(object):
    def maxSumOfThreeSubarrays(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: List[int]
        """
        max_sum = 0
        res = []
        for i in range(len(nums) - 3 * k + 1):
            for j in range(i + k, len(nums) - 2 * k + 1):
                for l in range(j + k, len(nums) - k + 1):
                    sum_i_j_l = sum(nums[i:i + k]) + sum(nums[j:j + k]) + sum(nums[l:l + k])
                    if sum_i_j_l > max_sum:
                        max_sum = sum_i_j_l
                        res = [i, j, l]
        return res


